mapper.select:
SELECT
    COUNT(1)
FROM
    bison.item_key
WHERE
    item_key = #{itemKey}
    AND type = #{type}
    AND business_unit_id = #{businessUnitId}

mapper.update:
UPDATE bison.item_key
    SET
    status = #{status}
WHERE
item_key_id = #{itemKeyId}

UPDATE bison.item_key
    SET
    item_key = #{itemKey},
    type = #{type}
WHERE
    item_key_id = #{itemKeyId}
