mapper.select:
select t1.*, c.country_name as countryName, p.product_model_number as productModelNumber, a.asin FROM ( SELECT spi.sale_plan_item_id as salePlanItemId, spi.country as countryId, spi.product_id as productId, spi.status, spi.remark, spi.est_units_promotion as estUnitsPromotion, rel.asin_id as asinId, u.user_id as userId, u.user_name as userName, up.total_safety_day as totalSafetyDay FROM bison.sale_plan sp, bison.sale_plan_item spi, bison_amz_product_inventory.user_product_amz_asin_rel rel, bison.user u, bison_amz_product_inventory.user_products up where spi.product_id = rel.product_id and sp.sale_plan_id = spi.sale_plan_id and spi.country = rel.country_id and spi.user_id = u.user_id and rel.product_id = up.product_id and u.user_id = #{userId} and spi.status > 2
and spi.country in
#{countryId}
and spi.product_id in
#{productId}
group by spi.sale_plan_item_id,spi.country,spi.product_id,rel.asin_id
) t1 left join bison.product p on t1.productId = p.product_id left join bison.country c on t1.countryId = c.country_id left join bison_amz_product_inventory.amz_asin a on t1.asinId = a.asin_id
