mapper.select:
select t1.*, c.country_name as countryName, p.product_model_number as productModelNumber, a.asin
FROM ( SELECT spi.sale_plan_item_id as salePlanItemId, spi.country as countryId, spi.product_id as productId, spi.status, spi.remark, spi.est_units_promotion as estUnitsPromotion, rel.asin_id as asinId FROM bison_amz_product_inventory.sale_plan sp, bison_amz_product_inventory.sale_plan_item spi , bison_amz_product_inventory.user_product_amz_asin_rel rel where spi.product_id = rel.product_id and sp.sale_plan_id = spi.sale_plan_id and spi.country = rel.country_id
and sp.sale_plan_id = #{salePlanId}
and spi.country in
#{countryId}
and spi.product_id in
#{productId}
and spi.status = #{status}
group by spi.sale_plan_item_id,spi.country,spi.product_id,rel.asin_id) t1
left join bison.product p on t1.productId = p.product_id left join bison.country c on t1.countryId = c.country_id left join bison_amz_product_inventory.amz_asin a on t1.asinId = a.asin_id

SELECT
    sum(units_ordered)
FROM
    bison_reports.amz_report_conversion_rate
where
asin_id = #{asinId}
and country_id = #{countryId}

    and DATE_SUB(CURDATE(), INTERVAL #{lastDayVal} DAY) <=  date(record_date)


group by asin_id;

select ik.item_key_id as itemKeyId, ik.item_key as itemKey, ik.last_day_val as lastDayVal, ik.type, ik.status
from bison.user u, bison_amz_product_inventory.item_key ik
where u.business_unit_id = ik.business_unit_id and u.user_id = #{userId}

select t1.*, c.country_name as countryName, p.product_model_number as productModelNumber, a.asin
FROM ( SELECT spi.sale_plan_item_id as salePlanItemId, spi.country as countryId, spi.product_id as productId, spi.status, spi.remark, spi.est_units_promotion as estUnitsPromotion, rel.asin_id as asinId, u.user_id as userId, u.user_name as userName FROM bison_amz_product_inventory.sale_plan sp, bison_amz_product_inventory.sale_plan_item spi, bison_amz_product_inventory.user_product_amz_asin_rel rel, bison.user u where spi.product_id = rel.product_id and sp.sale_plan_id = spi.sale_plan_id and spi.country = rel.country_id and spi.user_id = u.user_id and u.business_unit_id = #{businessUnitId}
AND #{startPalnDate} <= DATE_FORMAT(sp.plan_date,"%Y-%m") AND DATE_FORMAT(sp.plan_date,"%Y-%m") <= #{endPalnDate}
and spi.country in
#{countryId}
and spi.product_id in
#{productId}
and spi.status = #{status}
group by spi.sale_plan_item_id,spi.country,spi.product_id,rel.asin_id
) t1 left join bison.product p on t1.productId = p.product_id left join bison.country c on t1.countryId = c.country_id left join bison_amz_product_inventory.amz_asin a on t1.asinId = a.asin_id
